import React, { Component } from 'react'
import Header from '../components/common/Header/Header'
import BaseLayout from '../components/layouts/BaseLayout'
import axios from 'axios'
export class Portfolio extends Component {

    static async getInitialProps() {
        // this console log will only show up in the SERVER side not the client. The only way for this to show up on the client is when you access this component again.
        console.log('get initial props')
         
        let posts ={}
        try {
            const response = await axios.get('https://jsonplaceholder.typicode.com/posts')
            posts = response.data
            console.log(response.data);
          } catch (error) {
            console.error(error)
          }
        //take note at the end of this line, this function is an async task. This means that its execution lives outside and its execution is not sequential to the original
        //turned getInitialProps into an async function. through the try catch function, it runs the code and if theres an error anywhere, throw a console log of the error.
        // I am throwing userData to the component as a props
        return {posts: posts.splice(0,10)}
    }

    renderPosts(posts) {
       
        return posts.map( (post) => {
            return (
                <li>{post.title}</li>
            )
        })
    }

    render() {
        
        const {posts} = this.props
        return (
            <div> 
                <BaseLayout>
                    <h1>I am portfolio page</h1>
                    <ul>
                        deb
                       {this.renderPosts(posts)}
                    </ul>
                </BaseLayout>
            </div>
        )
    }
}

export default Portfolio
