import React, { Component } from 'react'
import BaseLayout from '../components/layouts/BaseLayout'
import {withRouter} from 'next/router'

export class portfolio extends Component {
    static getInitialProps({query}) {
        return {query}
      }
    
    render() {
        console.log(this.props)
        return (
            <div>
                <BaseLayout>
                    <h1>I am portfolio page</h1>
                    {/* 
                        now that we have a component thats thrown withRouter() we now receive a router object as a property,
                        inside router we have access to query which is what we built in the URL. specifically "title"
                     */}
                    <h2>{this.props.router.query.id}</h2>
                </BaseLayout>
            </div>
        )
    }
}

// in the docs you will find that the page needs to be surrounded with the withRouter() however we can also do this syntax in order to export the whole component wrapped inside withRouter()
export default withRouter(portfolio)
